def getMarkerData(request):
	## Get Data From Backend
	description = {
		'Hotel Rishika':{'lat':-77.032,'long':38.913,'contact':9920866202,'Address': 'Washington, D.C'},
		'Hotel Rushabh':{'lat':-122.414,'long':37.776,'contact':7977702709,'Address': 'San Fransisco'},
		'Hotel Ishwar':{'lat':-100.032,'long':37.913,'contact':8412042005,'Address': 'Ulhasnagar'},
		'Hotel Jinang':{'lat':-87.32,'long':38.6,'contact':9082005374,'Address': 'Kandivali'}
	}
	features = []
	for key,value in description.items():
		featureValue = {}
		featureValue['type'] = 'Feature'
		featureValue['geometry'] = {'type':'Point','coordinates':[value['lat'],value['long']]}
		featureValue['properties'] = {'title':key,'description':'Contact Number - '+str(value['contact'])+' Address - '+value['Address']}
		features.append(featureValue)
	data = {}
	data['type'] = 'FeatureCollection'
	data['features'] = features
	print(data)
	return JsonResponse(data,safe=False)

